Code:
{code}
RAMDirectory ramDirectory = new RAMDirectory();
IndexWriter indexWriter = new IndexWriter(ramDirectory, false, system.getDefaultAnalyzer(), true);
indexWriter.setMergeScheduler(new SerialMergeScheduler());
